            {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    name,
                    password,
                })
            })
        console.log(res)
    }

    signInWithPopup(auth, provider)
        .then((result) => {
            console.log(result.user.accessToken);
            const name = result.user.displayName;
            const email = result.user.email;
            const profilePic = result.user.photoURL;

        })
        .catch((error) => {
